import React, { Component } from 'react';
import logo from './logo.svg';
import Web3 from 'web3';
import './App.css';
import {ELECTION_ADDRESS,ELECTION_ABI} from './config';


class App extends Component{
  componentWillMount(){
    this.loadBlockChainData()
  }

  async loadBlockChainData(){
    const web3 = new Web3 (Web3.givenProvider)
    const network = await web3.eth.net.getNetworkType()
    console.log("network :::", network)
    const accounts = await web3.eth.getAccounts().then(console.log);
    const blockno  = await web3.eth.getBlockNumber().then(console.log);
    console.log("value of the account:", web3.eth.getBalance("0x3AE9f696BFdEB675627C6D326f89D53000ecd40B"))
    //console.log("value of the account:", web3.eth.getBalance(accounts[3]))
    //console.log("account 1:", accounts[0])
    //this.setState({account:'my val'})
    const elec = new web3.eth.Contract(ELECTION_ABI, ELECTION_ADDRESS)
    this.setState({elec})
    console.log('election ', elec)
    //console.log('owner of the contact', elec.method.owner)
    const myowner= await elec.methods.owner
    console.log('myowner', myowner )


  }

  constructor(props){
    super(props)
    this.state = {account:""}
  }

  render(){
    return(
      <div className="container">
        <h1> Hello world!</h1>
        <p>Account : {this.state.account}</p>
      </div>
    );
  }
}

export default App;
