import React, { Component } from 'react';
import logo from './logo.svg';
import Web3 from 'web3';
import './App.css';
import {ELECTION_ADDRESS,ELECTION_ABI} from './config';


class App extends Component{
  componentWillMount(){
    this.loadBlockChainData()
  }

  async loadBlockChainData(){
    const web3 = new Web3 (Web3.givenProvider || "http://localhost:8545")
    const network = await web3.eth.net.getNetworkType()
    console.log("network :::", network)
    const accounts = await web3.eth.accounts
    console.log("value of the account:", web3.eth.getBalance("0x3AE9f696BFdEB675627C6D326f89D53000ecd40B"))
    console.log("account 1:", accounts[0])
    this.setState({account:accounts[0]})
    const elec = new web3.eth.Contract(ELECTION_ABI, ELECTION_ADDRESS)
    this.setState({elec})
    console.log('election', elec)
  }

  constructor(props){
    super(props)
    this.state = {account:""}
  }

  render(){
    return(
      <div className="container">
        <h1> Hello world!</h1>
        <p>Account : {this.state.account}</p>
      </div>
    );
  }
}

export default App;
